namespace Sfynx\CmfBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Sfynx\CoreBundle\Repository\TranslationRepository;

/**
 * Tag Repository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 * 
 * @package    Cmf
 * @subpackage Repository
 * @author     Etienne de Longeaux <etienne.delongeaux@gmail.com>
 * @since      2012-01-06
 */
class TagRepository extends TranslationRepository
{
    /**
     * Return all tags.
     *
     * @return array
     * @access public
     * @author Etienne de Longeaux <etienne.delongeaux@gmail.com>
     * @since  2012-01-23
     */
    public function getAllTags()
    {
        $query = $this->createQueryBuilder('k')
        ->select('k')
        ->where('k.enabled = :enabled')
        ->orderBy('k.groupname', 'ASC')
        ->setParameter('enabled', 1);
    
        return $query;
    } 
    
    /**
     * Gets all groupname of tag.
     *
     * @return array
     * @access public
     * @author Etienne de Longeaux <etienne.delongeaux@gmail.com>
     * @since  2012-03-28
     */
    public function getArrayAllGroupName($locale)
    {
        $query = $this->createQueryBuilder('t')
        ->select('t.groupname')
        ->where('t.enabled = :enabled')
        ->setParameters(array(
            'enabled'    => 1,
        ))
        ->getQuery();
        
        $result = array();
        $data     = $this->findTranslationsByQuery($locale, $query, 'array', true);
        
        if ($data && is_array($data) && count($data)) {
            foreach ($data as $row) {
                if (!empty($row['groupname']))
                    $result[ $row['groupname'] ] = $row['groupname'];
            }
        }
        
        return $result;
    }
}
