namespace Cmf\ContentBundle\Repository;

use Doctrine\ORM\EntityRepository;
use BootStrap\TranslationBundle\Repository\TranslationRepository;

/**
 * ArticleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 * 
 * @category   plugins_Repositories
 * @package    Repository
 * 
 * @author Etienne de Longeaux <etienne.delongeaux@gmail.com>
 */
class ArticleRepository extends TranslationRepository
{
	public function findFlux()
	{
		$em = $this->createQueryBuilder('a')
		->select('a')
		->leftJoin('a.blocgeneral', 'b')
		->where('b.enabled = 1')
		->orderBy("a.position");
		
		return  $em->getQuery()->execute();
	}
}
